nesting--; if (nesting == 0) { BEGIN(INITIAL); return (int)DotToken.ID; }
            break;
        case 41:
{}
            break;
        case 42:
BEGIN(INITIAL); return (int)DotToken.LINECOMMENT;
            break;
        case 43:
        case 44:
{}
            break;
        case 45:
BEGIN(INITIAL); return (int)DotToken.MLINECOMMENT;
            break;
        default:
            break;
    }
#pragma warning restore 162, 1522
#endregion
                    }
                }
            } // end try
            finally {
// User-specified epilog to scan()
/* Epilog from LEX file */
    LoadYylVal();
// End, user-specified epilog
            } // end finally
        }

        void SaveStateAndPos(ref Context ctx)
        {
            ctx.bPos  = buffer.Pos;
            ctx.rPos  = readPos;
            ctx.cCol  = cCol;
            ctx.lNum  = lNum;
            ctx.state = state;
            ctx.cChr  = code;
        }

        void RestoreStateAndPos(ref Context ctx)
        {
            buffer.Pos = ctx.bPos;
            readPos = ctx.rPos;
            cCol  = ctx.cCol;
            lNum  = ctx.lNum;
            state = ctx.state;
            code  = ctx.cChr;
        }


        // ============= End of the tokenizer code ================

        [SuppressMessage("Microsoft.Performance", "CA1811:AvoidUncalledPrivateCode")]
        internal void ECHO() { Console.Out.Write(yytext); }
        
        } // end class DotLex
} // end namespace
