        // ============== The main tokenizer code =================

        int Scan() {
            try {
                for (; ; ) {
                    int next;              // next state to enter
#if LEFTANCHORS
                    for (;;) {
                        // Discard characters that do not start any pattern.
                        // Must check the left anchor condition after *every* GetCode!
                        state = ((cCol == 0) ? anchorState[currentScOrd] : currentStart);
                        if ((next = NextState()) != goStart) break; // LOOP EXIT HERE...
                        GetCode();
                    }
                    
#else // !LEFTANCHORS
                    state = currentStart;
                    while ((next = NextState()) == goStart) {
                        // At this point, the current character has no
                        // transition from the current state.  We discard 
                        // the "no-match" char.   In traditional LEX such 
                        // characters are echoed to the console.
                        GetCode();
                    }
#endif // LEFTANCHORS                    
                    // At last, a valid transition ...    
                    MarkToken();
                    state = next;
                    GetCode();                    
#if BACKUP
                    bool contextSaved = false;
                    while ((next = NextState()) > eofNum) { // Exit for goStart AND for eofNum
                        if (state <= maxAccept && next > maxAccept) { // need to prepare backup data
                            // Store data for the *latest* accept state that was found.
                            SaveStateAndPos( ref ctx );
                            contextSaved = true;
                        }
                        state = next;
                        GetCode();
                    }
                    if (state > maxAccept && contextSaved)
                        RestoreStateAndPos( ref ctx );
#else  // BACKUP
                    while ((next = NextState()) > eofNum) { // Exit for goStart AND for eofNum
                         state = next;
                         GetCode();
                    }
#endif // BACKUP
                    if (state <= maxAccept) {
                        MarkEnd();
#region ActionSwitch
#pragma warning disable 162, 1522
    switch (state)
    {
        case eofNum:
            if (yywrap())
                return (int)Tokens.EOF;
            break;
        case 1:
        case 2:
        case 5:
        case 18:
RaiseUnexpectedCharError(yytext);
            break;
        case 3:
        case 4:
        case 21:
        case 22:
return (int) MkId(yytext);
            break;
        case 6:
BEGIN(QSTRING); BuilderInit(); BuilderAppend();
            break;
        case 7:
return (int)DotTokenKind.ListStart;
            break;
        case 8:
return (int)DotTokenKind.ListEnd;
            break;
        case 9:
return (int)DotTokenKind.ScopeStart;
            break;
        case 10:
return (int)DotTokenKind.ScopeEnd;
            break;
        case 11:
return (int)DotTokenKind.Comma;
            break;
        case 12:
return (int)DotTokenKind.Semicolon;
            break;
        case 13:
return (int)DotTokenKind.Colon;
            break;
        case 14:
return (int)DotTokenKind.Equal;
            break;
        case 15:
BEGIN(HTML); nesting = 1; BuilderInit(); BuilderAppend();
            break;
        case 16:
BEGIN(LINECOMMENT); BuilderInit(); BuilderAppend();
            break;
        case 17:
return (int)DotTokenKind.Whitespace;
            break;
        case 19:
BEGIN(LINECOMMENT); BuilderInit(); BuilderAppend();
            break;
        case 20:
BEGIN(MLINECOMMENT); BuilderInit(); BuilderAppend();
            break;
        case 23:
return (int) DotTokenKind.Arrow;
            break;
        case 24:
return (int) DotTokenKind.Arrow;
            break;
        case 25:
        case 27:
BuilderAppend();
            break;
        case 26:
BEGIN(INITIAL); BuilderAppend(); tokTxt = BuilderBuild(); return (int)DotTokenKind.Id;
            break;
        case 28:
BuilderAppend();
            break;
        case 29:
BuilderAppend();
            break;
        case 30:
BuilderAppend();
            break;
        case 31:
nesting--; 
                     BuilderAppend();
                     if (nesting == 0) {
                       BEGIN(INITIAL);
                       tokTxt = BuilderBuild();
                       return (int)DotTokenKind.Id;
                     }
            break;
        case 32:
nesting++; 
                     BuilderAppend();
            break;
        case 33:
BuilderAppend();
            break;
        case 34:
_yytrunc(1); tokTxt = BuilderBuild(); BEGIN(INITIAL); return (int)DotTokenKind.Comment;
            break;
        case 35:
        case 36:
BuilderAppend();
            break;
        case 37:
BuilderAppend(); tokTxt = BuilderBuild(); BEGIN(INITIAL); return (int)DotTokenKind.MultilineComment;
            break;
        default:
            break;
    }
#pragma warning restore 162, 1522
#endregion
                    }
                }
            } // end try
            finally {
// User-specified epilog to scan()
/* Epilog from LEX file */
    LoadYylVal();
// End, user-specified epilog
            } // end finally
        }

#if BACKUP
        void SaveStateAndPos(ref Context ctx) {
            ctx.bPos  = buffer.Pos;
            ctx.rPos  = readPos;
            ctx.cCol  = cCol;
            ctx.lNum  = lNum;
            ctx.state = state;
            ctx.cChr  = code;
        }

        void RestoreStateAndPos(ref Context ctx) {
            buffer.Pos = ctx.bPos;
            readPos = ctx.rPos;
            cCol  = ctx.cCol;
            lNum  = ctx.lNum;
            state = ctx.state;
            code  = ctx.cChr;
        }
#endif  // BACKUP

